triples:
PREFIX top: <http://binkhoffmann.org/ontologies/top#>
PREFIX core: <http://otl.noord-holland.nl/core/>
PREFIX rdf:  <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs:  <http://www.w3.org/2000/01/rdf-schema#>
PREFIX owl:  <http://www.w3.org/2002/07/owl#>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX inst: <http://binkhoffmann.org/sample/>

core:Wegvak
	rdf:type owl:Class ;
	core:wordtGerealiseerdDoor core:Wegdeel ;
	rdfs:label "Wegvak" ; 
	rdfs:subClassOf core:WegLink ; .

core:Wegdeel
	rdf:type owl:Class ;
	core:realiseert core:Wegvak ;
	rdfs:label "Wegdeel" ; 
	 rdfs:subClassOf top:FysiekObject ; .

core:wordtGerealiseerdDoor
	rdf:type owl:ObjectProperty ;
	rdfs:label "wordt gerealiseerd door" ; .

core:realiseert
	rdf:type owl:ObjectProperty ;
	rdfs:label "realiseert" ; .

top:DrieDRuimte
  rdf:type owl:Class ;
  rdfs:label "Drie DObject" ;
  rdfs:subClassOf top:FysiekeRuimte ;
.
core:FunctioneelGebied
  rdf:type owl:Class ;
  rdfs:label "Functioneel gebied" ;
  rdfs:subClassOf top:DrieDRuimte ;
.
top:FysiekObject
  rdf:type owl:Class ;
  rdfs:label "Fysiek object" ;
  rdfs:subClassOf top:FysiekeRuimte ;
.
top:FysiekeRuimte
  rdf:type owl:Class ;
  rdfs:label "Fysieke ruimte" ;
  rdfs:subClassOf top:Object ;
.
core:NetwerkGebied
  rdf:type owl:Class ;
  rdfs:label "Netwerk gebied" ;
  rdfs:subClassOf core:FunctioneelGebied ;
.
core:NetwerkLink
  rdf:type owl:Class ;
  rdfs:label "Netwerk link" ;
  rdfs:subClassOf core:NetwerkGebied ;
.
core:WegLink
  rdf:type owl:Class ;
  rdfs:label "Weg link" ;
  rdfs:subClassOf core:NetwerkLink ;
.
top:Object
    rdf:type owl:Class ;	
   rdfs:subClassOf owl:Thing ;
.


inst:eenWegvak
	rdf:type core:Wegvak ; 
	rdfs:label "eenWegvak" ; 
	core:wordtGerealiseerdDoor inst:eenWegdeel ; .

inst:eenWegdeel
	rdf:type core:Wegdeel ; 
	rdfs:label "eenWegdeel" ; .
